import { View, Text, Image } from 'react-native'
import { SIZES,FONTS,COLORS,SHADOWS,assets } from '../constants'

export const NFTTitle = ({title, subTitle, titleSize, subTitleSize}) => {
  return (
    <View>
      <Text style={{ fontfamily:FONTS.semiBold, fontSize:titleSize, color:COLORS.primary }}>{title}</Text>
      <Text style={{ fontfamily:FONTS.regular, fontSize:subTitleSize, color:COLORS.primary }}>{subTitle}</Text>
    </View>
  )
}

export const EthPrice = ({price}) => {
    return (
      <View style={{ flexDirection: 'row', alignItems: 'center' }}>
        <Image 
            
        />
      </View>
    )
}

export const ImageCmp = ({imgUrl,index}) => {
    return (
      <Image 
        source={imgUrl}
        resizeMode="contain"
        style={{ 
            width:48,
            height:48,
            marginLeft: index == 0 ? 0 : -SIZES.font
         }}
      />
    )
}

export const People = () => {
    return (
      <View style={{ flexDirection: 'row' }}>
        {[assets.person02,assets.person03,assets.person04].map((imgUrl, index) => (
            <ImageCmp imgUrl={imgUrl} index={index} key='People-${index}'/>
        ))}
      </View>
    )
}
  
export const EndDate = () => {
    return (
      <View style={{ 
        paddingHorizontal: SIZES.font,
        paddingVertical: SIZES.base,
        backgroundColor: COLORS.white,
        justifyContent: 'center',
        alignItems: 'center',
        ...SHADOWS.light,
        elevation:1,
        maxWidth:'50%'
       }}>
        <Text style={{ 
            fontfamily: FONTS.regular, 
            fontSize:SIZES.small, 
            color: COLORS.primary }}>Ending In</Text>
        <Text style={{ 
            fontfamily: FONTS.semiBold, 
            fontSize:SIZES.medium, 
            color: COLORS.primary }}>12h 30m</Text>
      </View>
    )
}

export const SubInfo = () => {
    return (
      <View style={{
        width: '100%',
        paddingHorizontal: SIZES.font,
        marginTop: -SIZES.extraLarge,
        flexDirection: "row",
        justifyContent: 'space-between'
       }}>
        <People />
        <EndDate />
        <Text>SubInfo</Text>
      </View>
    )
}
