import React, {useState} from 'react'
import { Text, View, SafeAreaView, FlatList, TouchableOpacity, Image } from 'react-native'
import {assets, COLORS, NFTData} from '../../constants';
import { NFTCard,HomeHeader,FocusedStatusBar,Opsi } from '../../components';

const Home = ({ navigation }) => {
  return (
    <SafeAreaView style={{ flex:1 }}>
      <FocusedStatusBar background={"COLORS.primary"} />

      <View style={{ flex:1 }}>
        <View style={{ zIndex:0 }}>
          <FlatList 
            data={NFTData} 
            renderItem={({ item }) => <NFTCard data={item} />}
            keyExtractor={(item) => item.id} 
            showsVerticalScrollIndicator={false}
            ListHeaderComponent={<HomeHeader />}
          />
        </View>
        <View style={{ flexDirection:"row", justifyContent: "space-around"}}>
          <TouchableOpacity style={{ width:80, height:80, margin:10, backgroundColor:"#9CCF89", alignItems: 'center', justifyContent:'center' }}>
            <Image source={assets.car}
              resizeMode="contain"
              style={{ width:40,height:40 }}/>
          </TouchableOpacity>
          <TouchableOpacity style={{ width:80, height:80, margin:10, backgroundColor:"#9CCF89", alignItems: 'center', justifyContent:'center' }}>
            <Image source={assets.car}
              resizeMode="contain"
              style={{ width:40,height:40 }}/>
          </TouchableOpacity>
          <TouchableOpacity style={{ width:80, height:80, margin:10, backgroundColor:"#9CCF89", alignItems: 'center', justifyContent:'center' }}>
            <Image source={assets.car}
              resizeMode="contain"
              style={{ width:40,height:40 }}/>
          </TouchableOpacity>
          <TouchableOpacity style={{ width:80, height:80, margin:10, backgroundColor:"#9CCF89", alignItems: 'center', justifyContent:'center' }}>
            <Image source={assets.car}
              resizeMode="contain"
              style={{ width:40,height:40 }}/>
          </TouchableOpacity>
        </View>
        <View style={{ 
          position:"absolute",
          top: 0,
          bottom: 0,
          right:0,
          left:0,
          zIndex:-1,
         }}>
          <View style={{ height:300,backgroundColor:"#99A3D2" }}/>
          <View style={{ flex:1,backgroundColor:COLORS.white }}/>
        </View>
      </View>
    </SafeAreaView>
  )
}

export default Home
